import axios from 'axios';

export default async function sendPost(url='',data={}){
        let resp;
        try{
                const api = await axios.post(url,data)
			.then((response) => {
				if (response.ok){ 
					return response.json
				}
                		throw new Error(api.statusText);
			});//
        }catch(err){
                return (err.message ? err.message : resp);
        }       
}
