import axios from 'axios';

export default async function sendPost(url='',data={}){
	await axios.post(url,data)
		.then(response => {
			if (response.ok){ 
				return response.json;
			}
                	throw new Error(response.statusText);
		})
		.catch(err => {
			return (err.message)?err.message:'unknown error';
		});
}
